def get_theme_number(name: str) -> int:
	if name == "Mindaro":
		return 0
	themes = get_themes()
	for i, theme in enumerate(themes):
		if theme[1] == name:
			return i + 1
	return 0

def get_theme_name(number: int) -> str:
	if number == 0:
		return "Mindaro"
	number -= 1
	themes = get_themes()
	logging.info((number, themes))
	if len(themes) > number:
		return themes[number][1]
	return ""

