def sort_ass(h, invert=False, custom_list=None, custom_name=""):
	global default_playlist

	if custom_list is None:
		if pl_is_locked(pctl.active_playlist_viewing):
			show_message(_("Playlist is locked"))
			return

		name = gui.pl_st[h][0]
		playlist = pctl.multi_playlist[pctl.active_playlist_viewing].playlist_ids
	else:
		name = custom_name
		playlist = custom_list

	key = None
	ns = False

	if name == "Filepath":
		key = key_filepath
		if use_natsort:
			key = key_fullpath
			ns = True
	if name == "Filename":
		key = key_filepath  # key_filename
		if use_natsort:
			key = key_fullpath
			ns = True
	if name == "Artist":
		key = key_artist
	if name == "Album Artist":
		key = key_album_artist
	if name == "Title":
		key = key_title
	if name == "Album":
		key = key_album
	if name == "Composer":
		key = key_composer
	if name == "Time":
		key = key_duration
	if name == "Date":
		key = key_date
	if name == "Genre":
		key = key_genre
	if name == "#":
		key = key_t
	if name == "S":
		key = key_scrobbles
	if name == "P":
		key = key_playcount
	if name == "Starline":
		key = best
	if name == "Rating":
		key = key_rating
	if name == "Comment":
		key = key_comment
	if name == "Codec":
		key = key_codec
	if name == "Bitrate":
		key = key_bitrate
	if name == "Lyrics":
		key = key_hl
	if name == "❤":
		key = key_love
	if name == "Disc":
		key = key_disc
	if name == "CUE":
		key = key_cue

	if custom_list is None:
		if key is not None:

			if ns:
				key = natsort.natsort_keygen(key=key, alg=natsort.PATH)

			playlist.sort(key=key, reverse=invert)

			pctl.multi_playlist[pctl.active_playlist_viewing].playlist_ids = playlist
			default_playlist = pctl.multi_playlist[pctl.active_playlist_viewing].playlist_ids

			pctl.playlist_view_position = 0
			logging.debug("Position changed by sort")
			gui.pl_update = 1

	elif custom_list is not None:
		playlist.sort(key=key, reverse=invert)

	reload()

