class DConsole:
    def __init__(self):
        self.messages = []
        self.show = False
    def print(self, message, level=0):
        
        if len(self.messages) > 50:
            del self.messages[0]
        
        dtime = datetime.datetime.now()
        self.messages.append((message, level, dtime, Timer()))
        if level > 0:
            print(message)

