def uid_gen():
    return random.randrange(1, 100000000)

def pl_gen(title='Default',
           playing=0,
           playlist=None,
           position=0,
           hide_title=0,
           selected=0,
           parent="",
           hidden=False):

    if playlist == None:
        playlist = []

    return copy.deepcopy([title, playing, playlist, position, hide_title, selected, uid_gen(), [], hidden, False, parent])

