def sort_ass(h, invert=False, custom_list=None, custom_name=""):
    global default_playlist

    if custom_list is None:
        if pl_is_locked(pctl.active_playlist_viewing):
            show_message("Playlist is locked")
            return

        name = gui.pl_st[h][0]
        playlist = pctl.multi_playlist[pctl.active_playlist_viewing][2]
    else:
        name = custom_name
        playlist = custom_list

    key = None

    if name == "Artist":
        key = key_artist
    if name == "Album Artist":
        key = key_album_artist
    if name == "Title":
        key = key_title
    if name == "Album":
        key = key_album
    if name == "Composer":
        key = key_composer
    if name == "Time":
        key = key_duration
    if name == "Date":
        key = key_date
    if name == "Genre":
        key = key_genre
    if name == "T":
        key = key_t
    if name == "P":
        key = key_playcount
    if name == 'Starline':
        key = best
    if name == 'Rating':
        key = key_rating
    if name == 'Comment':
        key = key_comment
    if name == "Codec":
        key = key_codec
    if name == "Bitrate":
        key = key_bitrate
    if name == "Lyrics":
        key = key_hl
    if name == "❤":
        key = key_love

    if custom_list is None:
        if key is not None:

            playlist.sort(key=key, reverse=invert)

            pctl.multi_playlist[pctl.active_playlist_viewing][2] = playlist
            default_playlist = pctl.multi_playlist[pctl.active_playlist_viewing][2]

            pctl.playlist_view_position = 0
            console.print("DEBUG: Position changed by sort")
            gui.pl_update = 1

    elif custom_list is not None:
            playlist.sort(key=key, reverse=invert)


def sort_dec(h):
    sort_ass(h, True)


