        y = y0 + 215 * gui.scale

        self.toggle_square(x, y, toggle_transcode_output, _("Save to output folder"))
        y += 25 * gui.scale
        self.toggle_square(x, y, toggle_transcode_inplace, _("Save and overwrite files inplace"))

