                si = 7 * gui.scale
                gap = 5 * gui.scale
                left = 15 * gui.scale

                ddt.rect_r((left, left, si, si), [100,80,240,255], True)
                ddt.rect_r((left + gap + si, left, si, si), [100,80,240,255], True)
                ddt.rect_r((left + (gap + si) * 2, left, si, si), [100,80,240,255], True)

